/**
 * Rewrite of review model to clean product cache after making review
 *
 * @category   Netresearch
 * @package    Netresearch_CatalogCache
 * @author     Netresearch <info@netresearch.de>
 */
class Netresearch_CatalogCache_Model_Review extends Mage_Review_Model_Review
{
    protected function _afterSave()
    {
        parent::_afterSave();
        foreach($this->getProductCollection() as $product) {
            $product->cleanCache();
        }
    }
}